import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AdminDashboard from '../components/AdminDashboard';
import AgencyDashboard from '../components/AgencyDashboard';
import axios from 'axios';

const Dashboard = () => {
  const [role, setRole] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login'); // If not logged in, redirect to login page
    } else {
      const fetchUserRole = async () => {
        try {
          const response = await axios.get('http://localhost:5000/api/agency/profile', {
            headers: { Authorization: `Bearer ${token}` },
          });
          setRole(response.data.role);  // Set the role (admin/agency)
          setLoading(false);            // Stop loading
        } catch (error) {
          console.error('Error fetching user role:', error);
          setError('Failed to fetch user role');
          setLoading(false);
        }
      };
      fetchUserRole();
    }
  }, [navigate]);

  if (loading) return <p>Loading dashboard...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <header>
        <button onClick={() => {
          localStorage.removeItem('token');
          navigate('/login');
        }}>
          Logout
        </button>
      </header>
      <main>
        {role === 'admin' ? <AdminDashboard /> : <AgencyDashboard />}
      </main>
    </div>
  );
};

export default Dashboard;
