import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const toggleStatus = async (id) => {
    const token = localStorage.getItem('token');
    await axios.put(`http://localhost:5000/api/agency/admin/agency/${id}/toggle`, {}, {
      headers: { Authorization: `Bearer ${token}` },
    });
    window.location.reload();  // Reload the page to reflect status change
  };
  const [agencies, setAgencies] = useState([]);

  useEffect(() => {
    const fetchAgencies = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await axios.get('http://localhost:5000/api/agency/admin/agencies', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setAgencies(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchAgencies();
  }, []);

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      <ul>
        {agencies.map((agency) => (
          <li key={agency._id}>
            {agency.agencyDetails.name} - {agency.email}
            <button onClick={() => toggleStatus(agency._id)}>
              {agency.isActive ? 'Deactivate' : 'Activate'}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );


};

export default AdminDashboard;
